    //         case '가격 낮은 순':
    //             setSortedData(prev => {
    //                 return prev.sort((a, b) => a.price - b.price)
    //             });
    //             break;
    //         case '최신 순':
    //             setSortedData(prev => prev.sort((a, b) => new Date(b) - new Date(a)))
    //             break;
    //         case '오래된 순':
    //             setSortedData(prev => prev.sort((a, b) => new Date(a) - new Date(b)))
