import { useState, useReducer, useEffect } from 'react';
import './App.css';
import SubmitForm from './components/SubmitForm';
import ItemList from './components/ItemList';
import SortButtons from './components/SortButtons';

function App() {
  console.log('App')
  const testData = [
    {
      id: 9999999999991,
      name: '과자',
      price: 3500,
      category: '식료품',
      date: '2024-02-12',
      memoText: '쿠팡에서 구매',
      repurchase: true,
    },
    {
      id: 9999999999992,
      name: '각티슈',
      price: 10800,
      category: '생활용품',
      date: '2024-02-10',
      memoText: '마트에서 구매',
      repurchase: false,
    },
    {
      id: 9999999999993,
      name: '무드등',
      price: 25000,
      category: '인테리어',
      date: '2024-02-2',
      memoText: '오늘의 집에서 구매',
      repurchase: true,
    }
  ]

  const [formData, setFormData] = useState([...testData]);
  const [sortFilter, setSortFilter] = useState({
    'category': 'all',
    'sort': 'id',
    'startDate': '',
    'endDate': ''
  })

  const categoryOptions = ['식료품', '생활용품', '미용', '인테리어'];
  const sortOptions = ['가격 높은 순', '가격 낮은 순', '최신 순', '오래된 순']

  return (
    <div className="App">
      <SubmitForm formData={formData} setFormData={setFormData} categoryOptions={categoryOptions}/>
      <SortButtons setSortFilter={setSortFilter} categoryOptions={categoryOptions} sortOptions={sortOptions}/>
      <ItemList formData={formData} sortFilter={sortFilter}/>
    </div>
  );
}

export default App;
